mapper.select:
select 
from "WADIS_CMS"."CMS_CRESCENDO_TYPE"
where S_MARCHE = #{marche}
  and S_LANG = #{langue}
  and S_TYPE_ID = #{type}
  and I_ACTIF = 1

select 
from "WADIS_CMS"."CMS_CRESCENDO_TYPE"
where S_MARCHE = #{marche}
  and S_LANG = #{langue}
  and I_ACTIF = 1

mapper.update:
    update "WADIS_CMS"."CMS_CRESCENDO_TYPE"
       set S_LIBELLE = #{libelle}
	where S_MARCHE = #{marche}
	  and S_LANG = #{langue}
	  and S_TYPE_ID = #{type}
	  and I_ACTIF = #{actif}
	  and S_LIBELLE = #{libelle}

    update "WADIS_CMS"."CMS_CRESCENDO_TYPE"
       set I_ACTIF = true
	where S_MARCHE = #{marche}
	  and S_LANG = #{langue}
	  and S_TYPE_ID = #{type}
	  and S_LIBELLE = #{libelle}

    update "WADIS_CMS"."CMS_CRESCENDO_TYPE"
       set I_ACTIF = false
	where S_MARCHE = #{marche}
	  and S_LANG = #{langue}
	  and S_TYPE_ID = #{type}
	  and S_LIBELLE = #{libelle}

mapper.delete:
delete from "WADIS_CMS"."CMS_CRESCENDO_TYPE"
where S_MARCHE = #{marche}
  and S_LANG = #{langue}
  and S_TYPE_ID = #{type}
  and I_ACTIF = #{actif}
